import React, { FC, useEffect } from "react";
import logo from "./logo.svg";
import "./App.css";
import { AppDispatch, AppState, useAppDispatch, useAppSelector } from "./store";
import { initialise, real, unreal } from "./app.slice";
import { connect, ConnectedProps } from "react-redux";

const idSelector = (state: AppState) => state.imageID;

type Props = ConnectedProps<typeof connector>;

const App: FC<Props> = ({ markReal, markUnreal }) => {
  const dispatch = useAppDispatch();
  const id = useAppSelector(idSelector);

  useEffect(() => {
    dispatch(initialise());
  }, []);

  const imageURL = `http://localhost:3000/api/v1/image?id=${id}`;

  return (
    <div className="App">
      <div className={"active-image"}>
        <img src={imageURL} />
      </div>
      <div>
        <button onClick={markReal}>Real</button>
        <button onClick={markUnreal}>Not real</button>
      </div>
      <div></div>
    </div>
  );
};

const mapStateToProps = (state: AppState) => {
  return {
    imageURL: `http://localhost:3000/api/v1/image?id=${state.imageID}`,
  };
};

const mapDispatchToProps = (dispatch: AppDispatch) => {
  return {
    markReal: () => dispatch(real()),
    markUnreal: () => dispatch(unreal()),
  };
};

const connector = connect(mapStateToProps, mapDispatchToProps);

export default connect(mapStateToProps, mapDispatchToProps)(App);
