import { createSlice, Dictionary } from "@reduxjs/toolkit";
import axios from "axios";
import { AppState, createAppThunk } from "./store";
import * as modal from "./modal/modal.slice";

type Marked = { imageID: string; reasonIDs: string[] };

type Reason = { id: string; label: string };

interface TrainerState {
  imageID: number;
  screen: "pick" | "modal" | "report";
  marked: Marked[];
  reasons: Dictionary<Reason>;
}

const initialState: TrainerState = {
  imageID: 0,
  screen: "pick",
  marked: [],
  reasons: {
    bg: { id: "bg", label: "Background" },
    eyes: { id: "eyes", label: "Eyes" },
  },
};

const appSlice = createSlice({
  name: "trainer",
  initialState,
  reducers: {
    real: (state) => {
      state.imageID = (state.imageID + 1) % 16;
    },
    unreal: (state) => {
      state.screen = "modal";
    },
  },
  extraReducers: (builder) => {
    builder.addCase(modal.cancel, (state) => {
      state.screen = "pick";
    });

    builder.addCase(modal.confirm, (state, action) => {
      // Return to screen
      // Add to marked array (with reasons)

      state.screen = "pick";
    });
  },
});

export const confirm = createAppThunk("modal/confirm", (_, { getState }) => {
  const state = getState();

  const selectedReasons = Object.entries(state.modal.selected)
    .filter(([key, val]) => !!val)
    .map(([key]) => state.modal.reasons[key]);

  if (state.modal.otherReasonLabel) {
    const reason: Reason = {
      id: nanoid(),
      label: state.modal.otherReasonLabel,
    };
  }
});

export const initialise = createAppThunk("trainer/init", async () => {
  const response = await axios.get("http://localhost:3000/api/v1/image?id=1", {
    responseType: "stream",
  });
  console.log(response);
});

export const { real, unreal } = appSlice.actions;

export default appSlice.reducer;
