import { createSlice, Dictionary, PayloadAction } from "@reduxjs/toolkit";
import axios from "axios";
import { AppState, createAppThunk } from "../store";

type Reason = { id: string; label: string };

interface ModalState {
  reasons: Reason[];
  selected: Dictionary<boolean>;
}

const initialState: ModalState = {
  reasons: [
    { id: "bg", label: "Background" },
    { id: "eyes", label: "Eyes" },
  ],
  selected: {},
};

const modalSlice = createSlice({
  name: "modal",
  initialState,
  reducers: {
    toggleReason: (state, action: PayloadAction<{ id: string }>) => {
      state.selected[action.payload.id] = !state.selected[action.payload.id];
    },
    cancel: (state) => {},
    confirm: (state, action: PayloadAction<{ other?: Reason }>) => {
      state.selected = {};
      if (action.payload.other) {
        state.reasons.push(action.payload.other);
      }
    },
  },
});

export const { cancel, confirm, toggleReason } = modalSlice.actions;

export default modalSlice.reducer;
