import React, { FC, useEffect, useState } from "react";
import logo from "./logo.svg";
import {
  AppDispatch,
  AppState,
  useAppDispatch,
  useAppSelector,
} from "../store";
import styles from "./modal.module.css";
import { connect, ConnectedProps } from "react-redux";
import { toggleReason } from "./modal.slice";
type Props = ConnectedProps<typeof connector>;

const Modal: FC<Props> = ({ show, reasons, selected, select }) => {
  const [checked, setChecked] = useState<boolean[]>(
    new Array(reasons.length).fill(false)
  );

  const handleChange = (index: number) => {
    const nextChecked = checked.map((val, i) => {
      return i === index ? !val : val;
    });
    setChecked(nextChecked);
  };

  if (!show) return null;
  return (
    <div className={styles.modal}>
      <h3>Why</h3>
      <div>
        {reasons.map((r, i) => (
          <label>
            {r.label}
            <input
              value={r.id}
              checked={!!selected[r.id]}
              onChange={() => select(r.id)}
              type="checkbox"
            />
          </label>
        ))}
      </div>
    </div>
  );
};

const mapStateToProps = (state: AppState) => {
  return {
    show: state.app.screen === "modal",
    reasons: state.modal.reasons,
    selected: state.modal.selected,
  };
};

const mapDispatchToProps = (dispatch: AppDispatch) => {
  return {
    select: (id: string) => {
      dispatch(toggleReason({ id }));
    },
  };
};

const connector = connect(mapStateToProps, mapDispatchToProps);

export default connect(mapStateToProps, mapDispatchToProps)(Modal);
